document.addEventListener('DOMContentLoaded', function() {
    var form = document.querySelector('form.mbr-form');
    var alertSuccess = document.querySelector('div[data-form-alert]');
    var alertError = document.querySelector('div[data-form-alert-danger]');

    function showAlert(message, success = true) {
        if(success) {
            alertSuccess.innerHTML = message; // Set the success message dynamically
            alertSuccess.hidden = false; // Show the success alert
            alertError.hidden = true; // Ensure the error alert is hidden
        } else {
            alertError.innerHTML = message; // Set the error message dynamically
            alertError.hidden = false; // Show the error alert
            alertSuccess.hidden = true; // Ensure the success alert is hidden
        }
    }

    form.addEventListener('submit', function(event) {
        event.preventDefault(); // Prevent default form submission

        var formData = new FormData(form);
        var object = {};
        formData.forEach((value, key) => {
            object[key] = value;
        });
        var json = JSON.stringify(object);

        fetch('https://formspree.io/f/mleqwylp', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: json
        })
        .then(response => {
            if (response.ok) {
                return response.json();
            } else {
                throw new Error('Network response was not ok.');
            }
        })
        .then(data => {
            // Show success message using the showAlert function
            showAlert('Thanks for filling out the form! We will update you soon.');
            form.reset(); // Reset the form
            setTimeout(() => {
            alertSuccess.hidden = true; // Hide the success alert after 3 seconds
            }, 3000);
        })
        .catch(error => {
            // Show error message using the showAlert function
            showAlert('Oops... there was a problem submitting the form.', false);
            console.error('Error:', error);
            setTimeout(() => {
            alertError.hidden = true; // Hide the error alert after 3 seconds
            }, 3000);
        });
        });
    });
